      "code": "package com.tms.controller;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;
import com.tms.model.DataAnalytics;
import com.tms.service.DataAnalyticsService;
import javax.validation.Valid;
import java.util.List;

@RestController
@RequestMapping("/data-analytics")
@CrossOrigin(origins = "*")
public class DataAnalyticsController {

    @Autowired
    private DataAnalyticsService dataAnalyticsService;

    @GetMapping
    public ResponseEntity<List<DataAnalytics>> getAllDataAnalytics() {
        try {
            List<DataAnalytics> dataAnalytics = dataAnalyticsService.getAllDataAnalytics();
            return new ResponseEntity<>(dataAnalytics, HttpStatus.OK);
        } catch (Exception e) {
            return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @PostMapping
    public ResponseEntity<DataAnalytics> createDataAnalytics(@Valid @RequestBody DataAnalytics dataAnalytics) {
        try {
            if (dataAnalytics.getTrafficVolume() < 0 || dataAnalytics.getTravelTime() < 0 || dataAnalytics.getIncidentRate() < 0) {
                return new ResponseEntity<>(null, HttpStatus.BAD_REQUEST);
            }
            DataAnalytics created = dataAnalyticsService.createDataAnalytics(dataAnalytics);
            return new ResponseEntity<>(created, HttpStatus.CREATED);
        } catch (Exception e) {
            return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @GetMapping("/{id}")
    public ResponseEntity<DataAnalytics> getDataAnalyticsById(@PathVariable Long id) {
        try {
            if (id == null || id <= 0) {
                return new ResponseEntity<>(null, HttpStatus.BAD_REQUEST);
            }
            DataAnalytics dataAnalytics = dataAnalyticsService.getDataAnalyticsById(id);
            if (dataAnalytics != null) {
                return new ResponseEntity<>(dataAnalytics, HttpStatus.OK);
            } else {
                return new ResponseEntity<>(null, HttpStatus.NOT_FOUND);
            }
        } catch (Exception e) {
            return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @PutMapping("/{id}")
    public ResponseEntity<DataAnalytics> updateDataAnalytics(@PathVariable Long id, @Valid @RequestBody DataAnalytics dataAnalytics) {
        try {
            if (id == null || id <= 0) {
                return new ResponseEntity<>(null, HttpStatus.BAD_REQUEST);
            }
            if (dataAnalytics.getTrafficVolume() < 0 || dataAnalytics.getTravelTime() < 0 || dataAnalytics.getIncidentRate() < 0) {
                return new ResponseEntity<>(null, HttpStatus.BAD_REQUEST);
            }
            DataAnalytics updated = dataAnalyticsService.updateDataAnalytics(id, dataAnalytics);
            if (updated != null) {
                return new ResponseEntity<>(updated, HttpStatus.OK);
            } else {
                return new ResponseEntity<>(null, HttpStatus.NOT_FOUND);
            }
        } catch (Exception e) {
            return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @DeleteMapping("/{id}")
    public ResponseEntity<HttpStatus> deleteDataAnalytics(@PathVariable Long id) {
        try {
            if (id == null || id <= 0) {
                return new ResponseEntity<>(HttpStatus.BAD_REQUEST);
            }
            dataAnalyticsService.deleteDataAnalytics(id);
            return new ResponseEntity<>(HttpStatus.NO_CONTENT);
        } catch (Exception e) {
            return new ResponseEntity<>(HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @GetMapping("/traffic-volume/average")
    public ResponseEntity<Double> getAverageTrafficVolume() {
        try {
            List<DataAnalytics> analytics = dataAnalyticsService.getAllDataAnalytics();
            double average = analytics.stream().mapToInt(DataAnalytics::getTrafficVolume).average().orElse(0.0);
            return new ResponseEntity<>(average, HttpStatus.OK);
        } catch (Exception e) {
            return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @GetMapping("/incident-rate/high")
    public ResponseEntity<List<DataAnalytics>> getHighIncidentRateAreas() {
        try {
            List<DataAnalytics> analytics = dataAnalyticsService.getAllDataAnalytics();
            List<DataAnalytics> highIncidentAreas = analytics.stream()
                .filter(data -> data.getIncidentRate() > 0.1)
                .toList();
            return new ResponseEntity<>(highIncidentAreas, HttpStatus.OK);
        } catch (Exception e) {
            return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }
}",
